import React from "react";
import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function AccountContainer({allTransactions}) {

  const individualT = allTransactions.map((iTransact) => {
    return (
      <li key={iTransact.id} >
        {/* transaction={iTransact} */}
        date={iTransact.date}
        description={iTransact.description} 
        category={iTransact.category}
        amount={iTransact.amount} </li> 

    )
  })


  return (
    <div>
      <Search />
      <AddTransactionForm />
      {/* <TransactionsList 
      {allTransactions.map((iTransact) => (
        <TransactionsList key={iTransact.id}  transaction={iTransact} />
      ))} */}
      <TransactionsList transact={individualT} />
    </div>
  );
}

export default AccountContainer;
