import React, { useState } from "react";
import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function AccountContainer({ allTransactions }) {
  // console.log(allTransactions)

  //Initializing state variable for the transaction form data
  const [newTranscation, setNewTransaction] = useState({
    date: "",
    description: "",
    category: "",
    amount: "",
  });

  const handleSubmit = (event) => {
    event.preventDefault();

    // setNewTransaction({
    //   ...newTranscation,
    //   [event.target.name]: event.target.value
    // });

    // console.log(newTranscation)

    // Post new transaction
    fetch(`http://localhost:8001/transactions`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newTranscation),
    })
      .then((response) => {
        if (response.okay) {
          console.log("New transaction has been logged successfully");
        } else {
          console.error("Failed transaction");
        }
      })
      .catch((error) => {
        console.error("Error logging the transaction:", error);
      });

    //Reset fields submitted
    setNewTransaction({
      date: "",
      description: "",
      category: "",
      amount: "",
    });
  };

  // method to handle the newTransaction
  const handleTransaction = (event) => {
    const { date, description, category, amount } = e.target;
    setNewTransaction({
      ...newTranscation,
      [event.target.name]: event.target.value,
    });
  };

  return (
    <div>
      <Search />
      <AddTransactionForm addTransaction={handleSubmit} />
      <TransactionsList transaction={allTransactions} />
    </div>
  );
}

export default AccountContainer;
