import React,{useState} from "react";
import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function AccountContainer({ allTransactions }) {

  // console.log(allTransactions)

  //state for handling new transaction data
  const [newTranscation, setNewTransaction] = useState({
    date: "",
    description: "",
    category: "",
    amount: "",
  });

  // method to handle the newTransaction
  const handleTransaction = (event) => {
    setNewTransaction({
      ...newTranscation,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
  }


  return (
    <div>
      <Search />
      <AddTransactionForm addTransaction={handleTransaction}/>
      <TransactionsList transaction={allTransactions}/>
    </div>
  );
}

export default AccountContainer;
